import { applyMiddleware, combineReducers, createStore } from 'redux';
import reducerLogin from './Redusers/login'
import {ServiceUser} from './MiddleWare/serviceUser'
import reducerPost from './reducers/reducerPosts'

const reducer = combineReducers({reducerPost})

const store = createStore(reducer,applyMiddleware(ServiceUser,ServiceGetWeather))
window.store = store;
export default store;

